    public static byte[] base16Decoder(final String hex) {
        final byte[] bts = new byte[hex.length() / 2];
        for (int i = 0; i < bts.length; ++i) {
            bts[i] = (byte) Integer.parseInt(hex.substring(2 * i, 2 * i + 2), 16);
        }
        return bts;
    }

    public static String createToken(String privateKey,String appKey,String institution, String userId) {
        try {

            long accessTokenExpiryTimeMs = 123123123234L;

            Map<String, String> payload = new HashMap<>();
            payload.put(Constants.INST_CODE, institution);
            payload.put(Constants.USER_ID, userId);

            Algorithm algorithm= getAlgorithm(privateKey);
            Date accessTokenExpiry = (new Date(System.currentTimeMillis() + accessTokenExpiryTimeMs));

            String assertion = JWT.create()
                    .withIssuer(appKey)
                    .withPayload(payload)
                    .withExpiresAt(accessTokenExpiry)
                    .withIssuedAt(new Date())
                    .withNotBefore(new Date())
                    .sign(algorithm);

            return assertion;

        } catch (Exception e) {
            logger.error("[CommonHelper.java:131] Error in setting Expires in {}", e.getMessage(), e);
        }
        return null;
    }
